const LogView = ({ tasks, setTasks }) => {
  const [selectedDate, setSelectedDate] = useState(new Date());

  // Function to change the selected week
  const changeWeek = (direction) => {
    setSelectedDate((prevDate) => addDays(prevDate, direction * 7));
  };

  // Toggle the completion of a task
  const toggleTaskCompletion = (taskId) => {
    setTasks((prevTasks) =>
      prevTasks.map((task) =>
        task.id === taskId ? { ...task, completed: !task.completed } : task
      )
    );
  };

  // Get the dates for the current week
  const getWeekDates = () => {
    const start = startOfWeek(selectedDate, { weekStartsOn: 0 });
    return Array.from({ length: 7 }, (_, i) => addDays(start, i));
  };

  // Function to format the date for display
  const formatDate = (date) => {
    return format(date, 'EEE dd MMM');
  };

  // Function to add a new task
  const addTask = (date) => {
    const newTask = {
      id: Date.now().toString(),
      title: `Task ${tasks.length + 1}`,
      completed: false,
      date: format(date, 'yyyy-MM-dd'), // Ensuring date is formatted correctly
    };
    setTasks((prevTasks) => [...prevTasks, newTask]);
  };

  // Handling the drag-and-drop event
  const onDragEnd = (result) => {
    if (!result.destination) return;

    const updatedTasks = Array.from(tasks);
    const [reorderedItem] = updatedTasks.splice(result.source.index, 1);
    reorderedItem.date = result.destination.droppableId; // Update task date to match the new droppable (date)
    updatedTasks.splice(result.destination.index, 0, reorderedItem);
    setTasks(updatedTasks);
  };

  return (
    <div className="p-4 bg-gray-100">
      <h1 className="text-2xl font-bold mb-4">Log page</h1>
      <div className="bg-white rounded-lg shadow p-4">
        <div className="flex justify-between items-center mb-4">
          <button onClick={() => changeWeek(-1)} className="p-2 rounded-full hover:bg-gray-200">
            <ChevronLeft />
          </button>
          <span className="font-bold text-lg">Week of {format(selectedDate, 'MMMM d, yyyy')}</span>
          <button onClick={() => changeWeek(1)} className="p-2 rounded-full hover:bg-gray-200">
            <ChevronRight />
          </button>
        </div>

        {/* DragDropContext wraps all drag-and-drop components */}
        <DragDropContext onDragEnd={onDragEnd}>
          {/* Iterate over each date in the week */}
          {getWeekDates().map((date) => {
            const dateStr = format(date, 'yyyy-MM-dd'); // Format date as 'yyyy-MM-dd' for droppableId
            return (
              <div key={dateStr} className="mb-4">
                <div className="font-bold text-lg mb-2">{formatDate(date)}</div>

                {/* Each date is a droppable area */}
                <Droppable droppableId={dateStr}>
                  {(provided) => (
                    <ul {...provided.droppableProps} ref={provided.innerRef} className="min-h-[50px]">
                      {tasks
                        .filter((task) => task.date === dateStr) // Filter tasks by the current date
                        .map((task, taskIndex) => (
                          <Draggable key={task.id} draggableId={task.id} index={taskIndex}>
                            {(provided) => (
                              <li
                                ref={provided.innerRef}
                                {...provided.draggableProps}
                                {...provided.dragHandleProps}
                                className="flex items-center justify-between p-2 bg-white hover:bg-gray-100 rounded mb-2"
                              >
                                <div className="flex items-center">
                                  <input
                                    type="checkbox"
                                    checked={task.completed}
                                    onChange={() => toggleTaskCompletion(task.id)}
                                    className="mr-2"
                                  />
                                  <span>{task.title}</span>
                                </div>
                                <MoreVertical size={16} />
                              </li>
                            )}
                          </Draggable>
                        ))}
                      {provided.placeholder}
                    </ul>
                  )}
                </Droppable>
                <button
                  onClick={() => addTask(date)}
                  className="mt-2 text-blue-500 hover:text-blue-700"
                >
                  + Add Task
                </button>
              </div>
            );
          })}
        </DragDropContext>
      </div>
    </div>
  );
};

