  const [currentView, setCurrentView] = useState('home');
  const [tasks, setTasks] = useState([
    { id: 1, title: '08:00 max 2', completed: false, date: new Date().toDateString() },
    { id: 2, title: '10:30 Casa', completed: false, date: new Date().toDateString() },
    { id: 3, title: '12:00 Exercise', completed: false, date: new Date().toDateString() },
    { id: 4, title: '13:30 Learn Brilliant', completed: false, date: new Date().toDateString() },
  ]);
  const [streak, setStreak] = useState(0);

  return (
    <div className="max-w-md mx-auto bg-gray-100 h-screen flex flex-col">
      <div className="flex-1 overflow-y-auto">
        {currentView === 'home' && <HomePage tasks={tasks} setTasks={setTasks} streak={streak} setStreak={setStreak} />}
        {currentView === 'log' && <LogView tasks={tasks} setTasks={setTasks} />}
