import React, { useState, useEffect } from 'react';
import { Home, BookOpen, Settings as SettingsIcon, ChevronLeft, ChevronRight, MoreVertical, Calendar, Edit, Trash, PlusCircle } from 'lucide-react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

// Utility functions (getEncouragingPhrase and getEmoji remain the same)

// HomePage Component (remains largely the same, just update the import if needed)

// TaskPopup Component (remains the same)

// LogView Component (update the handleTaskAction function)
const LogView = ({ tasks, setTasks }) => {
  // ... (previous state and functions remain the same)

  const handleTaskAction = (action, task) => {
    switch (action) {
      case 'edit':
        const newTitle = prompt("Edit task title:", task.title);
        if (newTitle) {
          setTasks(tasks.map(t => t.id === task.id ? { ...t, title: newTitle } : t));
        }
        break;
      case 'reschedule':
        const newDate = prompt("Enter new date (YYYY-MM-DD):");
        if (newDate) {
          setTasks(tasks.map(t => t.id === task.id ? { ...t, date: new Date(newDate).toDateString() } : t));
        }
        break;
      case 'delete':
        if (window.confirm("Are you sure you want to delete this task?")) {
          setTasks(tasks.filter(t => t.id !== task.id));
        }
        break;
      case 'addNote':
        const note = prompt("Add a note to this task:");
        if (note) {
          setTasks(tasks.map(t => t.id === task.id ? { ...t, note: note } : t));
        }
        break;
      default:
        break;
    }
    setSelectedTask(null);
  };

  // ... (rest of the LogView component remains the same)
};

// New SettingsView Component
const SettingsView = ({ streak, settings, setSettings }) => {
  const toggleSetting = (setting) => {
    setSettings(prev => ({ ...prev, [setting]: !prev[setting] }));
  };

  return (
    <div className="p-4 bg-gray-100">
      <h1 className="text-2xl font-bold mb-4">Settings</h1>
      <div className="bg-white rounded-lg shadow p-4">
        <div className="mb-4">Current Record: {streak} days 🌱</div>
        <div className="space-y-2">
          <div className="flex justify-between">
            <span>Language</span>
            <span>Eng &gt;</span>
          </div>
          <div className="flex justify-between">
            <span>Week starts on</span>
            <span>{settings.weekStartsOn} &gt;</span>
          </div>
          <div className="flex justify-between">
            <span>Notification</span>
            <button onClick={() => toggleSetting('notification')}>
              {settings.notification ? 'On' : 'Off'} &gt;
            </button>
          </div>
          <div className="flex justify-between">
            <span>Sound</span>
            <button onClick={() => toggleSetting('sound')}>
              {settings.sound ? 'On' : 'Off'} &gt;
            </button>
          </div>
          <div className="flex justify-between">
            <span>Vibration</span>
            <button onClick={() => toggleSetting('vibration')}>
              {settings.vibration ? 'On' : 'Off'} &gt;
            </button>
          </div>
          <div className="flex justify-between">
            <span>Streak savers remaining</span>
            <span>{settings.streakSavers}</span>
          </div>
        </div>
      </div>
    </div>
  );
};

// Main App Component (updated to include settings state and SettingsView)
const App = () => {
  const [currentView, setCurrentView] = useState('home');
  const [tasks, setTasks] = useState([
    { id: 1, title: '08:00 max 2', completed: false, date: new Date().toDateString() },
    { id: 2, title: '10:30 Casa', completed: false, date: new Date().toDateString() },
    { id: 3, title: '12:00 Exercise', completed: false, date: new Date().toDateString() },
    { id: 4, title: '13:30 Learn Brilliant', completed: false, date: new Date().toDateString() },
  ]);
  const [streak, setStreak] = useState(0);
  const [settings, setSettings] = useState({
    language: 'Eng',
    weekStartsOn: 'Mon',
    notification: true,
    sound: true,
    vibration: true,
    streakSavers: 2
  });

  return (
    <div className="max-w-md mx-auto bg-gray-100 h-screen flex flex-col">
      <div className="flex-1 overflow-y-auto">
        {currentView === 'home' && <HomePage tasks={tasks} setTasks={setTasks} streak={streak} setStreak={setStreak} />}
        {currentView === 'log' && <LogView tasks={tasks} setTasks={setTasks} />}
        {currentView === 'settings' && <SettingsView streak={streak} settings={settings} setSettings={setSettings} />}
      </div>
      <div className="flex justify-around items-center p-4 bg-white">
        <button onClick={() => setCurrentView('home')} className="p-2 bg-green-200 rounded"><Home /></button>
        <button onClick={() => setCurrentView('log')} className="p-2 bg-purple-200 rounded"><BookOpen /></button>
        <button onClick={() => setCurrentView('settings')} className="p-2 bg-purple-200 rounded"><SettingsIcon /></button>
      </div>
    </div>
  );
};

export default App;
