import React, { useState, useEffect } from 'react';
import { Home, BookOpen, Settings, Calendar, Edit, Trash, Repeat, PlusCircle, Bell } from 'lucide-react';

// Modal Component
const Modal = ({ title, children, onClose }) => (
  <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
    <div className="bg-white rounded-lg p-4 w-80">
      <h3 className="text-lg font-semibold mb-4">{title}</h3>
      {children}
      <button onClick={onClose} className="mt-4 p-2 bg-gray-300 rounded">Close</button>
    </div>
  </div>
);

// Utility functions for notifications
const scheduleNotification = (title, time) => {
  const now = new Date();
  const notificationTime = new Date(time);
  
  const delay = notificationTime.getTime() - now.getTime();

  if (delay > 0) {
    setTimeout(() => {
      new Notification(title);
    }, delay);
  }
};

const requestNotificationPermission = async () => {
  if (Notification.permission !== "granted") {
    await Notification.requestPermission();
  }
};

// HomePage Component
const HomePage = ({ tasks, setTasks, streak, setStreak, weekStartsOn }) => {
  const [currentDate] = useState(new Date());
  const [selectedTask, setSelectedTask] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [modalType, setModalType] = useState('');

  useEffect(() => {
    requestNotificationPermission();
  }, []);

  const getWeekDates = (weekStartsOn) => {
    const today = new Date();
    const startDay = weekStartsOn === 'Mon' ? 1 : 0; // 1 for Monday, 0 for Sunday
    const currentDay = today.getDay();
    const difference = currentDay - startDay;

    const startOfWeek = new Date(today);
    startOfWeek.setDate(today.getDate() - difference);

    const week = [];
    for (let i = 0; i < 7; i++) {
      const date = new Date(startOfWeek);
      date.setDate(startOfWeek.getDate() + i);
      week.push(date);
    }

    return week;
  };

  const toggleTaskCompletion = (taskId) => {
    setTasks(prevTasks => {
      const updatedTasks = prevTasks.map(task =>
        task.id === taskId ? { ...task, completed: !task.completed } : task
      );

      const allCompleted = updatedTasks.every(task => task.completed);
      const wasAllCompleted = prevTasks.every(task => task.completed);

      if (allCompleted && !wasAllCompleted) {
        setStreak(prevStreak => prevStreak + 1);
      } else if (!allCompleted && wasAllCompleted) {
        setStreak(prevStreak => Math.max(0, prevStreak - 1));
      }

      return updatedTasks;
    });
  };

  // Add task notification functionality
  const addNotificationTime = (task, time) => {
    setTasks(prevTasks => prevTasks.map(t => (t.id === task.id ? { ...t, notificationTime: time } : t)));
    scheduleNotification(task.title, time);
  };

  const openModal = (task, type) => {
    setSelectedTask(task);
    setModalType(type);
    setShowModal(true);
  };

  const weekDates = getWeekDates(weekStartsOn);

  return (
    <div className="p-4 bg-gray-100">
      <h1 className="text-2xl font-bold mb-4">Home page</h1>
      <div className="bg-white rounded-lg shadow p-4 mb-4">
        <div className="text-4xl font-bold">{streak} days</div>
        <div className="flex justify-between mt-4">
          {weekDates.map((date, index) => (
            <div key={index} className="flex items-center space-x-2">
              <div className={`w-8 h-8 flex items-center justify-center rounded-full ${date.toDateString() === currentDate.toDateString() ? 'bg-yellow-400' : 'bg-gray-200'}`}>
                {date.getDate()}
              </div>
              {date.toDateString() === currentDate.toDateString() && (
                <button onClick={() => console.log('Add task logic here')}>
                  <PlusCircle size={20} />
                </button>
              )}
            </div>
          ))}
        </div>
      </div>
      <div className="bg-white rounded-lg shadow p-4">
        {tasks.map(task => (
          <div key={task.id} className="flex items-center justify-between mb-2">
            <div className="flex items-center">
              <input type="checkbox" checked={task.completed} onChange={() => toggleTaskCompletion(task.id)} className="mr-2" />
              <span>{task.title}</span>
            </div>
            <div className="flex space-x-2">
              <button onClick={() => openModal(task, 'edit')}><Edit size={18} /></button>
              <button onClick={() => openModal(task, 'notification')}><Bell size={18} /></button>
              <button onClick={() => openModal(task, 'reschedule')}><Calendar size={18} /></button>
              <button onClick={() => openModal(task, 'delete')}><Trash size={18} /></button>
              <button onClick={() => openModal(task, 'recurring')}><Repeat size={18} /></button>
            </div>
          </div>
        ))}
      </div>

      {showModal && selectedTask && modalType === 'notification' && (
        <Modal title="Set Notification Time" onClose={() => setShowModal(false)}>
          <input
            type="time"
            onChange={(e) => addNotificationTime(selectedTask, new Date(`${new Date().toDateString()} ${e.target.value}`))}
            className="p-2 border border-gray-300 rounded"
          />
        </Modal>
      )}

      {showModal && selectedTask && modalType === 'edit' && (
        <Modal title="Edit Task" onClose={() => setShowModal(false)}>
          <input
            type="text"
            defaultValue={selectedTask.title}
            onChange={(e) => console.log('Edit logic here')}
            className="p-2 border border-gray-300 rounded"
          />
        </Modal>
      )}

      {showModal && selectedTask && modalType === 'reschedule' && (
        <Modal title="Reschedule Task" onClose={() => setShowModal(false)}>
          <input type="date" onChange={(e) => console.log('Reschedule logic here')} className="p-2 border border-gray-300 rounded" />
        </Modal>
      )}

      {showModal && selectedTask && modalType === 'delete' && (
        <Modal title="Delete Task" onClose={() => setShowModal(false)}>
          <button onClick={() => console.log('Delete logic here')} className="p-2 bg-red-500 text-white rounded">
            Confirm Delete
          </button>
        </Modal>
      )}

      {showModal && selectedTask && modalType === 'recurring' && (
        <Modal title="Set Recurrence" onClose={() => setShowModal(false)}>
          <button onClick={() => console.log('Set to daily')} className="p-2 border border-gray-300 rounded">Daily</button>
          <button onClick={() => console.log('Set to weekly')} className="p-2 border border-gray-300 rounded">Weekly</button>
          <button onClick={() => console.log('Set to monthly')} className="p-2 border border-gray-300 rounded">Monthly</button>
        </Modal>
      )}
    </div>
  );
};

// LogView Component
const LogView = ({ tasks, setTasks, weekStartsOn }) => {
  const [selectedDate, setSelectedDate] = useState(new Date());

  const getWeekDates = (weekStartsOn) => {
    const today = new Date(selectedDate);
    const startDay = weekStartsOn === 'Mon' ? 1 : 0;
    const currentDay = today.getDay();
    const difference = currentDay - startDay;

    const startOfWeek = new Date(today);
    startOfWeek.setDate(today.getDate() - difference);

    const week = [];
    for (let i = 0; i < 7; i++) {
      const date = new Date(startOfWeek);
      date.setDate(startOfWeek.getDate() + i);
      week.push(date);
    }

    return week;
  };

  const changeWeek = (direction) => {
    const newDate = new Date(selectedDate);
    newDate.setDate(newDate.getDate() + direction * 7);
    setSelectedDate(newDate);
  };

  const weekDates = getWeekDates(weekStartsOn);

  return (
    <div className="p-4 bg-gray-100">
      <h1 className="text-2xl font-bold mb-4">Week {weekDates[0].getDate()}-{weekDates[6].getDate()}</h1>
      <div className="bg-white rounded-lg shadow p-4 mb-4">
        <div className="flex justify-between items-center">
          <button onClick={() => changeWeek(-1)} className="p-2 rounded-full hover:bg-gray-200">
            <ChevronLeft />
          </button>
          <span className="font-bold text-lg">Week {weekDates[0].getDate()}-{weekDates[6].getDate()}</span>
          <button onClick={() => changeWeek(1)} className="p-2 rounded-full hover:bg-gray-200">
            <ChevronRight />
          </button>
        </div>
      </div>

      {weekDates.map((date, index) => (
        <div key={index} className="mb-4">
          <div className="font-bold text-lg mb-2">{date.toDateString()}</div>
          <ul>
            {tasks
              .filter(task => task.date && new Date(task.date).toDateString() === date.toDateString())
              .map(task => (
                <li key={task.id} className="flex items-center justify-between p-2 hover:bg-gray-100 rounded">
                  <div className="flex items-center">
                    <input
                      type="checkbox"
                      checked={task.completed}
                      onChange={() => console.log('Toggle logic here')}
                      className="mr-2"
                    />
                    <span>{task.title}</span>
                  </div>
                  <div className="flex space-x-2">
                    <button onClick={() => console.log('Edit logic')}><Edit size={18} /></button>
                    <button onClick={() => console.log('Set notification')}><Bell size={18} /></button>
                    <button onClick={() => console.log('Reschedule logic')}><Calendar size={18} /></button>
                    <button onClick={() => console.log('Delete logic')}><Trash size={18} className="text-red-500" /></button>
                    <button onClick={() => console.log('Set recurrence')}><Repeat size={18} /></button>
                  </div>
                </li>
              ))}
          </ul>
        </div>
      ))}
    </div>
  );
};

// SettingsView Component
const SettingsView = ({ settings, setSettings }) => {
  return (
    <div className="p-4 bg-gray-100">
      <h1 className="text-2xl font-bold mb-4">Settings</h1>
      <div className="bg-white rounded-lg shadow p-4">
        <div className="mb-4">
          <span>Week Starts On: </span>
          <select
            value={settings.weekStartsOn}
            onChange={(e) => setSettings({ ...settings, weekStartsOn: e.target.value })}
            className="p-2 border border-gray-300 rounded"
          >
            <option value="Mon">Monday</option>
            <option value="Sun">Sunday</option>
          </select>
        </div>
        <div className="mb-4">
          <span>Notifications: </span>
          <button onClick={() => setSettings({ ...settings, notifications: !settings.notifications })} className="p-2 bg-gray-300 rounded">
            {settings.notifications ? 'On' : 'Off'}
          </button>
        </div>
      </div>
    </div>
  );
};

// Main App Component
const App = () => {
  const [currentView, setCurrentView] = useState('home');
  const [tasks, setTasks] = useState([
    { id: 1, title: '08:00 Task 1', completed: false, date: new Date().toDateString(), notificationTime: null },
    { id: 2, title: '10:30 Task 2', completed: false, date: new Date().toDateString(), notificationTime: null },
  ]);
  const [streak, setStreak] = useState(0);
  const [settings, setSettings] = useState({
    weekStartsOn: 'Mon',
    notifications: true,
  });

  return (
    <div className="max-w-md mx-auto bg-gray-100 h-screen flex flex-col">
      <div className="flex-1 overflow-y-auto">
        {currentView === 'home' && <HomePage tasks={tasks} setTasks={setTasks} streak={streak} setStreak={setStreak} weekStartsOn={settings.weekStartsOn} />}
        {currentView === 'log' && <LogView tasks={tasks} setTasks={setTasks} weekStartsOn={settings.weekStartsOn} />}
        {currentView === 'settings' && <SettingsView settings={settings} setSettings={setSettings} />}
      </div>
      <div className="flex justify-around items-center p-4 bg-white">
        <button onClick={() => setCurrentView('home')} className="p-2 bg-green-200 rounded"><Home /></button>
        <button onClick={() => setCurrentView('log')} className="p-2 bg-purple-200 rounded"><BookOpen /></button>
        <button onClick={() => setCurrentView('settings')} className="p-2 bg-purple-200 rounded"><Settings /></button>
      </div>
    </div>
  );
};

export default App;
