import React, { useState, useEffect } from 'react';
import { Home, BookOpen, Settings, Plus, MoreVertical } from 'lucide-react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import './styles/index.css';

const getEncouragingPhrase = () => {
  const phrases = ["You're proving your strength!", "Keep up the great work!", "You're on fire!"];
  return phrases[Math.floor(Math.random() * phrases.length)];
};

const getEmoji = () => {
  const emojis = ['ðŸŒ±', 'ðŸŒ¿', 'ðŸŒ³', 'ðŸŒ»'];
  return emojis[Math.floor(Math.random() * emojis.length)];
};

const playSuccessSound = () => {
  console.log('Playing success sound');
};

const HomePage = ({ tasks, setTasks }) => {
  const [streak, setStreak] = useState(0);
  const [emoji, setEmoji] = useState(getEmoji());
  const [encouragingPhrase, setEncouragingPhrase] = useState(getEncouragingPhrase());
  const [currentDate] = useState(new Date());

  useEffect(() => {
    const allCompleted = tasks.every(task => task.completed);
    if (allCompleted) {
      setStreak(prevStreak => prevStreak + 1);
      setEmoji(getEmoji());
      setEncouragingPhrase(getEncouragingPhrase());
      playSuccessSound();
    }
  }, [tasks]);

  const toggleTaskCompletion = (taskId) => {
    setTasks(prevTasks => {
      const updatedTasks = prevTasks.map(task =>
        task.id === taskId ? { ...task, completed: !task.completed } : task
      );
      
      if (!updatedTasks.every(task => task.completed) && prevTasks.every(task => task.completed)) {
        setStreak(prevStreak => Math.max(0, prevStreak - 1));
      }
      
      return updatedTasks;
    });
  };

  const getWeekDates = () => {
    const today = new Date(currentDate);
    const week = [];
    for (let i = 0; i < 7; i++) {
      const date = new Date(today);
      date.setDate(today.getDate() - today.getDay() + i);
      week.push(date);
    }
    return week;
  };

  return (
    <div className="p-4 bg-gray-100">
      <h1 className="text-2xl font-bold mb-4">Home page</h1>
      <div className="bg-white rounded-lg shadow p-4 mb-4">
        <div className="text-4xl font-bold">{streak} days {emoji}</div>
        <div className="text-sm text-gray-600">{encouragingPhrase}</div>
        <div className="flex justify-between mt-4">
          {getWeekDates().map((date, index) => (
            <div 
              key={index} 
              className={`w-8 h-8 flex items-center justify-center rounded-full ${
                date.toDateString() === currentDate.toDateString() 
                  ? 'bg-yellow-400' 
                  : tasks.every(task => task.completed) && date.toDateString() === currentDate.toDateString()
                  ? 'bg-green-400'
                  : 'bg-gray-200'
              }`}
            >
              {date.getDate()}
            </div>
          ))}
        </div>
      </div>
      <div className="bg-white rounded-lg shadow p-4">
        <div className="font-bold mb-2">
          {currentDate.toLocaleString('default', { month: 'short' })} {currentDate.getDate()} {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'][currentDate.getDay()]}
        </div>
        {tasks.map(task => (
          <div key={task.id} className="flex items-center justify-between mb-2">
            <div className="flex items-center">
              <input 
                type="checkbox" 
                checked={task.completed} 
                onChange={() => toggleTaskCompletion(task.id)}
                className="mr-2"
              />
              <span>{task.title}</span>
            </div>
            {task.completed && <span>âœ“</span>}
          </div>
        ))}
      </div>
    </div>
  );
};

const LogView = ({ tasks, setTasks }) => {
  const [selectedDay, setSelectedDay] = useState(new Date());
  const [showTaskMenu, setShowTaskMenu] = useState(null);

  const getWeekDates = () => {
    const today = new Date();
    const week = [];
    for (let i = 0; i < 7; i++) {
      const date = new Date(today);
      date.setDate(today.getDate() - today.getDay() + i);
      week.push(date);
    }
    return week;
  };

  const addTask = () => {
    const newTask = {
      id: Date.now(),
      title: `New Task ${tasks.length + 1}`,
      completed: false,
      date: selectedDay.toDateString()
    };
    setTasks([...tasks, newTask]);
  };

  const toggleTaskCompletion = (taskId) => {
    setTasks(tasks.map(task =>
      task.id === taskId ? { ...task, completed: !task.completed } : task
    ));
  };

  const onDragEnd = (result) => {
    if (!result.destination) return;
    const items = Array.from(tasks);
    const [reorderedItem] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItem);
    setTasks(items);
  };

  const handleTaskAction = (action, taskId) => {
    switch (action) {
      case 'edit':
        // Implement edit functionality
        break;
      case 'reschedule':
        // Implement reschedule functionality
        break;
      case 'delete':
        setTasks(tasks.filter(task => task.id !== taskId));
        break;
      default:
        break;
    }
    setShowTaskMenu(null);
  };

  return (
    <div className="p-4 bg-gray-100">
      <h1 className="text-2xl font-bold mb-4">Log View</h1>
      <div className="flex justify-between mb-4">
        {getWeekDates().map((date, index) => (
          <button
            key={index}
            onClick={() => setSelectedDay(date)}
            className={`px-2 py-1 rounded ${
              date.toDateString() === selectedDay.toDateString() 
                ? 'bg-blue-500 text-white' 
                : 'bg-gray-200'
            }`}
          >
            {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'][date.getDay()]}
          </button>
        ))}
      </div>
      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="tasks">
          {(provided) => (
            <ul {...provided.droppableProps} ref={provided.innerRef}>
              {tasks
                .filter(task => new Date(task.date).toDateString() === selectedDay.toDateString())
                .map((task, index) => (
                <Draggable key={task.id} draggableId={task.id.toString()} index={index}>
                  {(provided) => (
                    <li 
                      ref={provided.innerRef} 
                      {...provided.draggableProps} 
                      {...provided.dragHandleProps}
                      className="bg-white p-2 mb-2 rounded shadow flex items-center justify-between"
                    >
                      <div className="flex items-center">
                        <input 
                          type="checkbox" 
                          checked={task.completed} 
                          onChange={() => toggleTaskCompletion(task.id)}
                          className="mr-2"
                        />
                        <span>{task.title}</span>
                      </div>
                      <button onClick={() => setShowTaskMenu(task.id)}>
                        <MoreVertical size={16} />
                      </button>
                      {showTaskMenu === task.id && (
                        <div className="absolute right-0 mt-2 w-48 bg-white rounded-md shadow-lg z-10">
                          <div className="py-1">
                            <button onClick={() => handleTaskAction('edit', task.id)} className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 w-full text-left">Edit</button>
                            <button onClick={() => handleTaskAction('reschedule', task.id)} className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 w-full text-left">Reschedule</button>
                            <button onClick={() => handleTaskAction('delete', task.id)} className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 w-full text-left">Delete</button>
                          </div>
                        </div>
                      )}
                    </li>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </ul>
          )}
        </Droppable>
      </DragDropContext>
      <button 
        onClick={addTask}
        className="mt-4 bg-blue-500 text-white px-4 py-2 rounded flex items-center"
      >
        <Plus size={16} className="mr-2" /> Add Task
      </button>
    </div>
  );
};

