import React, { useState, useEffect } from 'react';
import { Home, BookOpen, Settings, MoreVertical, Calendar, Edit, Trash, PlusCircle, Repeat } from 'lucide-react';

// Utility functions
const getEncouragingPhrase = () => {
  const phrases = ["You're proving your strength!", "Keep up the great work!", "You're on fire!"];
  return phrases[Math.floor(Math.random() * phrases.length)];
};

const getEmoji = () => {
  const emojis = ['ðŸŒ±', 'ðŸŒ¿', 'ðŸŒ³', 'ðŸŒ»'];
  return emojis[Math.floor(Math.random() * emojis.length)];
};

// HomePage Component
const HomePage = ({ tasks, setTasks, streak, setStreak }) => {
  const [emoji, setEmoji] = useState(getEmoji());
  const [encouragingPhrase, setEncouragingPhrase] = useState(getEncouragingPhrase());
  const [currentDate] = useState(new Date());

  useEffect(() => {
    const allCompleted = tasks.every(task => task.completed);
    if (allCompleted) {
      setEmoji(getEmoji());
      setEncouragingPhrase(getEncouragingPhrase());
    }
  }, [tasks]);

  const toggleTaskCompletion = (taskId) => {
    setTasks(prevTasks => {
      const updatedTasks = prevTasks.map(task =>
        task.id === taskId ? { ...task, completed: !task.completed } : task
      );
      
      const allCompleted = updatedTasks.every(task => task.completed);
      const wasAllCompleted = prevTasks.every(task => task.completed);
      
      if (allCompleted && !wasAllCompleted) {
        setStreak(prevStreak => prevStreak + 1);
      } else if (!allCompleted && wasAllCompleted) {
        setStreak(prevStreak => Math.max(0, prevStreak - 1));
      }
      
      return updatedTasks;
    });
  };

  const getWeekDates = () => {
    const today = new Date(currentDate);
    const week = [];
    for (let i = 0; i < 7; i++) {
      const date = new Date(today);
      date.setDate(today.getDate() - today.getDay() + i);
      week.push(date);
    }
    return week;
  };

  return (
    <div className="p-4 bg-gray-100">
      <h1 className="text-2xl font-bold mb-4">Home page</h1>
      <div className="bg-white rounded-lg shadow p-4 mb-4">
        <div className="text-4xl font-bold">{streak} days {emoji}</div>
        <div className="text-sm text-gray-600">{encouragingPhrase}</div>
        <div className="flex justify-between mt-4">
          {getWeekDates().map((date, index) => (
            <div 
              key={index} 
              className={`w-8 h-8 flex items-center justify-center rounded-full ${
                date.toDateString() === currentDate.toDateString() 
                  ? 'bg-yellow-400' 
                  : tasks.every(task => task.completed) && date.toDateString() === currentDate.toDateString()
                  ? 'bg-green-400'
                  : 'bg-gray-200'
              }`}
            >
              {date.getDate()}
            </div>
          ))}
        </div>
      </div>
      <div className="bg-white rounded-lg shadow p-4">
        <div className="font-bold mb-2">
          {currentDate.toLocaleString('default', { month: 'short' })} {currentDate.getDate()} {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'][currentDate.getDay()]}
        </div>
        {tasks.filter(task => new Date(task.date).toDateString() === currentDate.toDateString()).map(task => (
          <div key={task.id} className="flex items-center justify-between mb-2">
            <div className="flex items-center">
              <input 
                type="checkbox" 
                checked={task.completed} 
                onChange={() => toggleTaskCompletion(task.id)}
                className="mr-2"
              />
              <span>{task.title}</span>
            </div>
            <div className="flex space-x-2">
              <button onClick={() => alert('Edit task')}><Edit size={18} /></button>
              <button onClick={() => alert('Reschedule task')}><Calendar size={18} /></button>
              <button onClick={() => alert('Delete task')}><Trash size={18} className="text-red-500" /></button>
              <button onClick={() => alert('Set task recurrence')}><Repeat size={18} /></button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

// TaskPopup Component
const TaskPopup = ({ task, onClose, onEdit, onReschedule, onDelete, onAddNote }) => {
  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-4 w-80">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-lg font-semibold">{task.title}</h3>
          <button onClick={onClose} className="text-gray-500">&times;</button>
        </div>
        <div className="space-y-2">
          <button onClick={onEdit} className="flex items-center w-full p-2 hover:bg-gray-100 rounded">
            <Edit size={18} className="mr-2" /> Edit Task
          </button>
          <button onClick={onReschedule} className="flex items-center w-full p-2 hover:bg-gray-100 rounded">
            <Calendar size={18} className="mr-2" /> Reschedule
          </button>
          <button onClick={onAddNote} className="flex items-center w-full p-2 hover:bg-gray-100 rounded">
            <PlusCircle size={18} className="mr-2" /> Add Note
          </button>
          <button onClick={onDelete} className="flex items-center w-full p-2 hover:bg-gray-100 rounded text-red-500">
            <Trash size={18} className="mr-2" /> Delete Task
          </button>
        </div>
      </div>
    </div>
  );
};

// LogView Component
const LogView = ({ tasks, setTasks }) => {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [selectedTask, setSelectedTask] = useState(null);

  const changeWeek = (direction) => {
    const newDate = new Date(selectedDate);
    newDate.setDate(newDate.getDate() + direction * 7);
    setSelectedDate(newDate);
  };

  const toggleTaskCompletion = (taskId) => {
    setTasks(tasks.map(task =>
      task.id === taskId ? { ...task, completed: !task.completed } : task
    ));
  };

  const getWeekDates = () => {
    const startOfWeek = new Date(selectedDate);
    startOfWeek.setDate(selectedDate.getDate() - selectedDate.getDay());
    const weekDates = [];
    for (let i = 0; i < 7; i++) {
      const date = new Date(startOfWeek);
      date.setDate(startOfWeek.getDate() + i);
      weekDates.push(date);
    }
    return weekDates;
  };

  const formatDate = (date) => {
    const days = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
    const months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
    return `${days[date.getDay()]} ${date.getDate()} ${months[date.getMonth()]}`;
  };

  const addTask = (date) => {
    const newTask = {
      id: Date.now(),
      title: `Task ${tasks.length + 1}`,
      completed: false,
      date: date.toDateString()
    };
    setTasks([...tasks, newTask]);
  };

  const handleTaskAction = (action, task) => {
    switch (action) {
      case 'edit':
        console.log('Edit task:', task);
        break;
      case 'reschedule':
        console.log('Reschedule task:', task);
        break;
      case 'delete':
        setTasks(tasks.filter(t => t.id !== task.id));
        break;
      case 'addNote':
        console.log('Add note to task:', task);
        break;
      default:
        break;
    }
    setSelectedTask(null);
  };

  return (
    <div className="p-4 bg-gray-100">
      <h1 className="text-2xl font-bold mb-4">Log page</h1>
      <div className="bg-white rounded-lg shadow p-4">
        <div className="flex justify-between items-center mb-4">
          <button onClick={() => changeWeek(-1)} className="p-2 rounded-full hover:bg-gray-200"><ChevronLeft /></button>
          <span className="font-bold text-lg">Week</span>
          <button onClick={() => changeWeek(1)} className="p-2 rounded-full hover:bg-gray-200"><ChevronRight /></button>
        </div>
        {getWeekDates().map((date, index) => (
          <div key={index} className="mb-4">
            <div className="font-bold text-lg mb-2">{formatDate(date)}</div>
            <ul>
              {tasks
                .filter(task => new Date(task.date).toDateString() === date.toDateString())
                .map((task, taskIndex) => (
                  <li key={task.id} className="flex items-center justify-between p-2 hover:bg-gray-100 rounded">
                    <div className="flex items-center">
                      <input
                        type="checkbox"
                        checked={task.completed}
                        onChange={() => toggleTaskCompletion(task.id)}
                        className="mr-2"
                      />
                      <span>{task.title}</span>
                    </div>
                    <div className="flex space-x-2">
                      <button onClick={() => alert('Edit task')}><Edit size={18} /></button>
                      <button onClick={() => alert('Reschedule task')}><Calendar size={18} /></button>
                      <button onClick={() => alert('Delete task')}><Trash size={18} className="text-red-500" /></button>
                      <button onClick={() => alert('Set task recurrence')}><Repeat size={18} /></button>
                    </div>
                  </li>
                ))}
            </ul>
            <button
              onClick={() => addTask(date)}
              className="mt-2 text-blue-500 hover:text-blue-700"
            >
              + Add Task
            </button>
            <hr className="my-2 border-gray-200" />
          </div>
        ))}
      </div>
      {selectedTask && (
        <TaskPopup
          task={selectedTask}
          onClose={() => setSelectedTask(null)}
          onEdit={() => handleTaskAction('edit', selectedTask)}
          onReschedule={() => handleTaskAction('reschedule', selectedTask)}
          onDelete={() => handleTaskAction('delete', selectedTask)}
          onAddNote={() => handleTaskAction('addNote', selectedTask)}
        />
      )}
    </div>
  );
};

// Main App Component
const App = () => {
  const [currentView, setCurrentView] = useState('home');
  const [tasks, setTasks] = useState([
    { id: 1, title: '08:00 max 2', completed: false, date: new Date().toDateString() },
    { id: 2, title: '10:30 Casa', completed: false, date: new Date().toDateString() },
    { id: 3, title: '12:00 Exercise', completed: false, date: new Date().toDateString() },
    { id: 4, title: '13:30 Learn Brilliant', completed: false, date: new Date().toDateString() },
  ]);
  const [streak, setStreak] = useState(0);

  return (
    <div className="max-w-md mx-auto bg-gray-100 h-screen flex flex-col">
      <div className="flex-1 overflow-y-auto">
        {currentView === 'home' && <HomePage tasks={tasks} setTasks={setTasks} streak={streak} setStreak={setStreak} />}
        {currentView === 'log' && <LogView tasks={tasks} setTasks={setTasks} />}
        {currentView === 'settings' && <div>Settings View (Not implemented)</div>}
      </div>
      <div className="flex justify-around items-center p-4 bg-white">
        <button onClick={() => setCurrentView('home')} className="p-2 bg-green-200 rounded"><Home /></button>
        <button onClick={() => setCurrentView('log')} className="p-2 bg-purple-200 rounded"><BookOpen /></button>
        <button onClick={() => setCurrentView('settings')} className="p-2 bg-purple-200 rounded"><Settings /></button>
      </div>
    </div>
  );
};

export default App;
